# ---
# 0/ Pour écrire les résultats de chaque réponse
#
def write_result_message(num_rep, phrase1, phrase2="")
  largeur = [ phrase1.to_s.length, phrase2.to_s.length ].max

  puts
  puts num_rep.to_s.center(largeur)
  puts "-=" * ( largeur / 2 + 1)
  puts phrase1.to_s.center(largeur)
  if phrase2.to_s != "" then puts phrase2.to_s.center(largeur) end
  puts "-=" * ( largeur / 2 + 1)
end


# ---
# 00/ Pour obtenir les tailles min ou max de handel dans un tableau
#
def get_boundary_size_handles(liste_handles,key_result)
  # On initialise avec la taille du premier handle
  min_length = liste_handles[0].length # @ compris
  max_length = liste_handles[0].length # @ compris | Pour l'exo 8

  liste_handles.each_with_index do | handle, index |

    if handle.length < min_length then
      min_length = handle.length
    elsif handle.length > max_length then
      max_length = handle.length
    end

  end

  if key_result == "min" then
    return min_length
  elsif key_result == "max" then
    return max_length
  end

end

