result_tab = []

journalists.each do | name |

  # Un nouveau tableau sans le '@'
  result_tab << name.sub(/@/,'')

end

result_tab = result_tab.sort

# Voici donc la liste de handles classées par ordre alphabétique
write_result_message(5,
  "Voici la liste des journalistes classés par ordre alphabétique... les 6 premiers !",
  result_tab[0..5].to_s)


# -----------------------------------------------------------------------------------------------------
# 6/ Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
# -----------------------------------------------------------------------------------------------------
result_tab = []
result_tab = journalists.sort_by { |mot| mot.length}

# Voici donc la liste des journalistes classés par tailles de handle
write_result_message(6,
