# ----------------------------------------------------------------
# 7/ Quelle est la position dans l'array de la personne @epenser ?
# ----------------------------------------------------------------
def is_handle_exist(tab_handle, name)

  if tab_handle.index(name) == nil then
    puts "#{name} n'est pas dans la liste"
  else
    return tab_handle.index(name)
  end

end

resul_index = is_handle_exist(journalists, "@epenser")
# La position est donc à + 1

write_result_message(7,
  " Le journaliste @epenser est en index #{resul_index} du tableau",
  "Il occupe donc la #{resul_index+1}ième position")

