# 8/ Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1
#    caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
# ------------------------------------------------------------------------------------------
# Je tente avec une table de hash


# Initialisation de la table de Hash (result_hash = {} // result_hash.default = 0)
result_hash = Hash.new(0)
chaine_message = ""

# On a déjà calculé la longueur maxi
max_length = get_boundary_size_handles(journalists,"max")


min_length.upto(max_length) do |taille|

  # compte le nombre de journaliste pour chaque taille de min à max  
  nombre = journalists.count { | handle | handle.length == taille }

  # rentre le dans result_hash[taille]=  
  if nombre != 0 then
    result_hash[taille] = nombre
    chaine_message = chaine_message + "#{taille} :" + "#{nombre}, " 
  end
end

# Voici donc répartition des handles par taille :  taille :nombre de handel,
write_result_message(8,
  "Voici la répartition des handles par taille : taille :nombre de handel !",
  chaine_message.to_s)

puts "\nSoit encore le Hash suivant :\n-----------------------------\n"
puts result_hash
